import React, { useState } from 'react';

function Counter() {
    const [count, setCount] = useState(0)

    function decreament() {
        setCount[count - 1]
    }

    function increament() {
        setCount[+1]
    }

    return (
        <div>
            <button onClick={increament}>Increament</button>
            <p>{count}</p>
            <button onClick={decreament}>Decrement</button>
        </div>
    )
}

export default Counter;
