import React, { useState } from 'react';

function Counter() {
    const [count, setCount] = useState(0)

    function decrement() {
        setCount(count - 1)
    }

    function increament() {
        setCount(count + 1)
    }


    return (
        <div>
            <button onClick={increament}>Increment</button>
            <p>{count}</p>
            <button onClick={decrement}>decrement</button>
        </div>
    )
}

export default Counter;
